drop table pc_0 ;
create table pc_0 as 
select distinct a.concept_code as pack_code,a.concept_name as pack_name, c.concept_name,c.concept_code,c.concept_Class_id,c.SOURCE_CONCEPT_CLASS_ID 
from drug_concept_stage a --3137
join SCT2_RELA_FULL_AU b on a.concept_code=sourceid
join drug_concept_stage c on c.concept_code=destinationid
WHERE a.concept_name like '%[Drug Pack]'
and c.concept_class_id='Drug Product'
and typeid!='116680003'
and c.SOURCE_CONCEPT_CLASS_ID in ('Trade Product Unit','Med Product Unit');--M Prod Pack and Cont Tr Prod Pack need to be excluded
;

drop table pc_0_1_1;
create table pc_0_1_1 as 
select pack_code,pack_name,concept_name,concept_code,pack_comp from (
select distinct
trim(regexp_substr( regexp_substr(PACK_NAME,'\[(\d)+.*\]')  , '[^&]+', 1, levels.column_value))  as pack_comp , concept_name, concept_code ,pack_code,pack_name
from pc_0 t,
table(cast(multiset(select level from dual connect by  level <= length (regexp_substr( regexp_substr(PACK_NAME,'\[(\d)+.*\]')  , '[^&]+'))  + 1) as sys.OdciNumberList)) levels)
where pack_name like '%(&)%';

drop table pc_0_1;
create table pc_0_1 as 
select pack_code,pack_name,concept_name,concept_code,pack_comp from (
select distinct
trim(regexp_substr( regexp_substr(PACK_NAME,'\(.*[0-9].*\)')  , '[^,]+', 1, levels.column_value))  as pack_comp , concept_name, concept_code ,pack_code,pack_name
from pc_0 t,
table(cast(multiset(select level from dual connect by  level <= length (regexp_substr( regexp_substr(PACK_NAME,'\(.*[0-9].*\)')  , '[^,]+'))  + 1) as sys.OdciNumberList)) levels)
where pack_code not in (select pack_code from pc_0_1_1);


drop table pc_0_2;
create table pc_0_2 as 
select distinct pack_code,pack_name,concept_name,concept_code,
case when regexp_like (regexp_substr (pack_comp,'(X\s)[0-9]+'),regexp_substr (concept_name,'[0-9]+')) then  replace(trim(regexp_substr (pack_comp,'[0-9]+\sX')),' X') else null end as amount 
from pc_0_1 ;

drop table pc_0_2_1;
create table pc_0_2_1 as 
select distinct pack_code,pack_name,concept_name,concept_code,
case when regexp_like (regexp_substr (pack_comp,'\[[0-9]+.*\]'),regexp_substr (concept_name,'[0-9]+'))
then  regexp_substr(trim(regexp_substr (pack_comp,'\[[0-9]+.*\]')), '\d+') else null end as amount 
from pc_0_1_1 ;

