drop table pc_0_3;
create table pc_0_3 as 
select pack_code,pack_name,concept_name,concept_code,amount, regexp_substr ( regexp_substr (pack_name,'([0-9]+\sX\s[0-9]+\s\[Drug Pack\])|([0-9]+\sX\s[0-9]+\sTablets\s\[Drug Pack\])|([0-9]+\sX\s[0-9]+\sTablets,\sBlister\sPacks\s\[Drug Pack\])|([0-9]+\sX\s[0-9]+,\sBlister\sPacks\s\[Drug Pack\])'), '[0-9]+') as box_size from pc_0_2;

update pc_0_3 set box_size='1' where pack_name like '%1 Pack%';

update pc_0_3
set amount='7' where CONCEPT_NAME like '%Inert%' and amount!='4';

delete pc_0_3 where PACK_CODE in (
select PACK_CODE  from pc_0_3 group by concept_code, PACK_CODE having count (1) > 1 )
and amount is null;

update pc_0_3 set amount=
case when concept_name like '%Day%' and amount is null then '16'
when concept_name like '%Night%' and amount is null then '8'
when pack_name like '%Ezetimibe%' and amount is null then '30'
when pack_name like '%24%4%28%' and amount is null then '24'
when pack_name like '%21%7%28%' and amount is null then '21'
when concept_name like '%Amox%' or concept_name like '%Nexium%'  and amount is null then '28'
when concept_name like '%Ziprasidone%'  and amount is null then '2' 
when concept_name like '%Peginterferon Alfa-2a%' or concept_name like '%Misoprostol%' and amount is null then '4' 
when concept_name like '%Esomeprazole%' or concept_name like '%Clarithromycin%' and amount is null then '14'
when concept_name like '%Bisacodyl%' or concept_name like '%Quinagolide%' and amount is null then '3'
when concept_name like '%Solution%' or concept_name like '%Cream%' or concept_name like '%Implant%' or concept_name like '%Mifepristone%' 
or concept_name like '%Pessary%'  or concept_name like '%Liquid%' or concept_name like '%Ointment%'  or concept_name like '%Vial%' or concept_name like '%Vagiclear%' or amount='0' then '1'
when concept_name like '%Rebetol%' then regexp_substr(regexp_substr (pack_name,'(\d)+\sX\sRebetol'),'\d+')
when concept_name like '%Peg-Intron%' then regexp_substr(regexp_substr (pack_name,'(\d)+\sX\sPeg-Intron'),'\d+')
when concept_name like '%Bonecal%' then regexp_substr(regexp_substr (pack_name,'(\d)+\sX\sBonecal'),'\d+')
when pack_name like '% X %Calcium%' and concept_name like '%Calcium%'then regexp_substr(regexp_substr (pack_name,'(\d)+\sX\sCalcium'),'\d+')
when pack_name like '% X %Calcium%[%]%' and concept_name like '%Calcium%' then regexp_substr(regexp_substr (pack_name,'(Calcium.*\[\d\])'),'\d+',1,2)
when pack_name like '%Ribavirin%[%]%' and concept_name like '%Ribavirin%'then regexp_substr(regexp_substr (pack_name,'((\d)+\sX\sRibavirin)|(Ribavirin.*\[\d\])'),'\d+',1,2)
when pack_name like '% X %Ribavirin%' and concept_name like '%Ribavirin%' then regexp_substr(regexp_substr (pack_name,'((\d)+\sX\sRibavirin)'),'\d+',1,1) 
when concept_name like '%Viekira%' then '112'
else amount end;

