--supplier
create table aut_supplier_1 as
select a.concept_code as concept_code_1,a.concept_name as concept_name_1, b.concept_id as concept_id_2,b.concept_name as concept_name_2,'1' as precedence from drug_concept_stage a
join devv5.concept b on lower(a.concept_name)=lower(b.concept_name)
where a.concept_class_id='Supplier'
and b.concept_class_id='Supplier'
and b.VOCABULARY_ID='RxNorm Extension';

create table aut_supplier_2 as
select distinct a.concept_code as concept_code_1,a.concept_name as concept_name_1, b.concept_id as concept_id_2,b.concept_name as concept_name_2,'1' as precedence
 from drug_concept_stage a join devv5.concept b on regexp_like (lower(b.concept_name),lower(a.concept_name))
where b.concept_class_id='Supplier'
and b.VOCABULARY_ID='RxNorm Extension'
and a.concept_class_id='Supplier'
and a.concept_code not in (select concept_code_1 from aut_supplier_1);

