create table aut_ingr_1 as
select distinct a.concept_code as concept_code_1,a.concept_name as concept_name_1, b.concept_id as concept_id_2,b.concept_name as concept_name_2,'1' as precedence from drug_concept_stage a
join devv5.concept b on lower(a.concept_name)=lower(b.concept_name)
where a.concept_class_id='Ingredient'
and b.concept_class_id='Ingredient'
and b.VOCABULARY_ID in ('RxNorm Extension','RxNorm')
and a.standard_concept='S'
and b.standard_concept='S';


create table aut_ingr_2 as
select distinct a.concept_code as concept_code_1,a.concept_name as concept_name_1, b.concept_id as concept_id_2,b.concept_name as concept_name_2,'1' as precedence
 from drug_concept_stage a join devv5.concept b on utl_match.edit_distance(lower(b.concept_name),lower(a.concept_name))<4
where a.concept_class_id='Ingredient'
and b.concept_class_id='Ingredient'
and b.VOCABULARY_ID in ('RxNorm Extension','RxNorm')
and a.standard_concept='S'
and b.standard_concept='S'
and concept_code_1 not in (select concept_code_1 from aut_ingr_1);


