create table ingr_to_map as
select * from drug_concept_stage where concept_code not in 
(select concept_code_1 from AUT_INGR_2 union select concept_code_1 from AUT_INGR_1 )
and concept_class_id='Ingredient' and standard_concept='S';



create table AUT_INGR_3 as
select distinct a.concept_code as concept_code_1,a.concept_name as concept_name_1,d.concept_id as concept_id_2,d.concept_name as concept_name_2,'1' as precedence
 from ingr_to_map a join devv5.concept b on lower(a.concept_name)=lower(b.concept_name)
join devv5.concept_relationship c on b.concept_id=c.concept_id_1
join devv5.concept d on concept_id_2=d.concept_id
where d.concept_class_id='Ingredient'
and d.invalid_reason is null
and d.vocabulary_id like'Rx%' ;

