create table aut_unit_1 as
select distinct a.*,COALESCE (b.CONCEPT_ID_2,c.CONCEPT_ID_2) as CONCEPT_ID_2, COALESCE (b.CONCEPT_NAME_2,c.CONCEPT_NAME_2) as CONCEPT_NAME_2, COALESCE (b.CONVERSION_FACTOR,c.CONVERSION_FACTOR) as CONVERSION_FACTOR, COALESCE (b.PRECEDENCE,c.PRECEDENCE) as PRECEDENCE
from (
select distinct amount_unit from ds_stage
union
select distinct numerator_unit from ds_stage
union 
select distinct denominator_unit from ds_stage) a 
left join dev_dmd.UNIT_FOR_UCUM_DONE b on lower(amount_unit)=lower(b.CONCEPT_NAME_1)
left join dev_amis.AUT_UNIT_ALL_MAPPED c on lower(amount_unit)=lower(c.CONCEPT_CODE)
where amount_unit is not null;

