-- Brand Names

create table aut_bn_1 as
select distinct a.concept_code as concept_code_1,a.concept_name as concept_name_1,b.concept_id as concept_id_2,b.concept_name as concept_name_2,'1' as precedence
from drug_concept_stage a join devv5.concept b
on lower(a.concept_name)=lower(b.concept_name)
where a.concept_class_id='Brand Name'
and b.concept_class_id='Brand Name'
and b.vocabulary_id like 'Rx%'
and b.invalid_reason is null;


create table aut_bn_2_1 as
select distinct  a.concept_code as concept_code_1,a.concept_name as concept_name_1,b.concept_id as concept_id_2,b.concept_name as concept_name_2,'1' as precedence
from drug_concept_stage a join devv5.concept b
on regexp_like (lower(a.concept_name),lower(b.concept_name))
where a.concept_code not in 
(select concept_code_1 from aut_bn_1)
and a.concept_class_id='Brand Name'
and b.concept_class_id='Brand Name'
and b.vocabulary_id like 'Rx%'
and b.invalid_reason is null;

delete aut_bn_2_1 where CONCEPT_ID_2 in (
select b.CONCEPT_ID_2 from aut_bn_2_1 a
join aut_bn_2_1 b on a.CONCEPT_CODE_1=b.CONCEPT_CODE_1
where a.CONCEPT_CODE_1 in (select CONCEPT_CODE_1 from aut_bn_2_1 group by concept_code_1 having count (1)>1)
and length(b.concept_name_2)<length(a.concept_name_2));
select * from aut_bn_2_1 a
where a.CONCEPT_CODE_1 in (select CONCEPT_CODE_1 from aut_bn_2_1 group by concept_code_1 having count (1)>1)
;

create table aut_bn_all_mapped as (
select concept_code_1,concept_id_2,precedence from aut_bn_2_1
union
select concept_code_1,concept_id_2,precedence from aut_bn_1);

